import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ShoppingListDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const defaultList = {
    name: 'Default Shopping List',
    items: [
      { id: 1, name: 'Default Item 1', resolved: false },
      { id: 2, name: 'Default Item 2', resolved: false },
      { id: 3, name: 'Default Item 3', resolved: true },
    ],
  };

  const { list = defaultList } = location.state || {};
  const [items, setItems] = useState(list.items || []);

  const toggleResolved = (id) => {
    setItems(
      items.map((item) =>
        item.id === id ? { ...item, resolved: !item.resolved } : item
      )
    );
  };

  const removeItem = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-4 sm:p-8 rounded-lg shadow-lg">
      <header className="flex items-center justify-between mb-6 border-b pb-4">
        <button
          onClick={() => navigate('/')}
          className="text-xl sm:text-2xl text-gray-700 hover:text-gray-900 transition"
        >
          ←
        </button>
        <h1 className="text-lg sm:text-3xl font-semibold text-gray-800">
          {list.name}
        </h1>
      </header>

      <ul className="divide-y divide-gray-200">
        {items.map((item) => (
          <li
            key={item.id}
            className="flex justify-between items-center py-2 sm:py-3"
          >
            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={item.resolved}
                onChange={() => toggleResolved(item.id)}
                className="form-checkbox h-4 w-4 sm:h-5 sm:w-5 text-blue-600"
              />
              <span
                className={`${
                  item.resolved ? 'line-through text-gray-500' : 'text-gray-800'
                }`}
              >
                {item.name}
              </span>
            </div>
            <div className="flex gap-4 items-center">
              {/* Archive Button */}
              <div
                className="relative group cursor-pointer"
                onClick={() => toggleResolved(item.id)}
              >
                <div
                  className={`w-6 h-6 rounded-full flex items-center justify-center transition ${
                    item.resolved
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-500 hover:bg-blue-100 hover:text-blue-500'
                  }`}
                >
                  ✓
                </div>
                <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                  {item.resolved ? 'Unarchive' : 'Archive'}
                </span>
              </div>

              {/* Delete Button */}
              <div
                className="relative group cursor-pointer"
                onClick={() => removeItem(item.id)}
              >
                <button className="w-6 h-6 rounded-full text-gray-500 bg-gray-100 border border-gray-300 flex items-center justify-center hover:text-red-500 hover:bg-red-100 hover:border-red-500 transition">
                  ✖
                </button>
                <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                  Delete
                </span>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListDetail;
