import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ShoppingListDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [list, setList] = useState(location.state?.list || {});
  const [items, setItems] = useState(list.items || []);

  const toggleArchive = () => {
    setList({ ...list, archived: !list.archived });
  };

  const deleteList = () => {
    alert('List deleted! Redirecting...');
    navigate('/');
  };

  return (
    <div className="max-w-6xl mx-auto bg-white p-6 sm:p-8 rounded-lg shadow-lg">
      <header className="flex justify-between items-center mb-6">
        <button
          onClick={() => navigate('/')}
          className="text-lg text-blue-500 hover:underline"
        >
          ‚Üê Back
        </button>
        <h1 className="text-2xl font-bold text-gray-800">{list.name}</h1>
      </header>

      <div className="flex justify-between items-center mb-6">
        <button
          onClick={toggleArchive}
          className={`px-4 py-2 rounded-md text-sm font-medium ${
            list.archived
              ? 'bg-yellow-400 text-gray-800 hover:bg-yellow-500'
              : 'bg-blue-400 text-white hover:bg-blue-500'
          } shadow-md hover:scale-105 transition`}
        >
          {list.archived ? 'Unarchive' : 'Archive'}
        </button>
        <button
          onClick={deleteList}
          className="px-4 py-2 bg-red-500 text-white rounded-md shadow-md hover:bg-red-600 hover:scale-105 transition"
        >
          Delete
        </button>
      </div>

      <ul className="divide-y divide-gray-200">
        {items.map((item) => (
          <li
            key={item.id}
            className="flex justify-between items-center py-3"
          >
            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={item.resolved}
                onChange={() =>
                  setItems((prevItems) =>
                    prevItems.map((i) =>
                      i.id === item.id
                        ? { ...i, resolved: !i.resolved }
                        : i
                    )
                  )
                }
                className="form-checkbox h-4 w-4 text-blue-600"
              />
              <span
                className={`${
                  item.resolved
                    ? 'line-through text-gray-500'
                    : 'text-gray-800'
                }`}
              >
                {item.name}
              </span>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListDetail;
