import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './UserContext';
import Modal from './Modal';
import data from './data.json';

const MainPage = () => {
  const { currentUser, setCurrentUser } = useContext(UserContext);
  const navigate = useNavigate();

  const users = [
    { id: 1, name: 'Owner', role: 'owner' },
    { id: 2, name: 'Guest', role: 'guest' },
  ];
  const [shoppingLists, setShoppingLists] = useState(data.shoppingLists || []);
  const [filter, setFilter] = useState('active');
  const [newListName, setNewListName] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [confirmDeleteList, setConfirmDeleteList] = useState(null);

  const addNewList = () => {
    if (newListName.trim() !== '') {
      const newList = {
        id: shoppingLists.length + 1,
        name: newListName,
        archived: false,
        items: [],
        members: [],
      };
      setShoppingLists([...shoppingLists, newList]);
      setNewListName('');
      setIsModalOpen(false);
    }
  };

  const toggleArchive = (id) => {
    setShoppingLists((prevLists) =>
      prevLists.map((list) =>
        list.id === id ? { ...list, archived: !list.archived } : list
      )
    );
  };

  const deleteList = () => {
    setShoppingLists((prevLists) =>
      prevLists.filter((list) => list.id !== confirmDeleteList)
    );
    setConfirmDeleteList(null);
  };

  const filteredLists = shoppingLists.filter(
    (list) => (filter === 'active' ? !list.archived : list.archived)
  );

  const goToShoppingListDetail = (list) => {
    navigate(`/shopping-list/${list.id}`, { state: { list } });
  };

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-8 rounded-lg shadow-lg relative">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Shopping App</h1>
        <select
          value={currentUser.id}
          onChange={(e) =>
            setCurrentUser(users.find((user) => user.id === parseInt(e.target.value)))
          }
          className="p-2 m-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.name} ({user.role})
            </option>
          ))}
        </select>
      </header>
      <div className="flex justify-between items-center mb-6">
        <div className="flex bg-gray-200 rounded-full p-1">
          <button
            onClick={() => setFilter('active')}
            className={`px-4 py-2 rounded-full focus:outline-none ${
              filter === 'active'
                ? 'bg-blue-500 text-white'
                : 'bg-transparent text-gray-700 hover:bg-gray-300'
            } transition`}
          >
            Active
          </button>
          <button
            onClick={() => setFilter('archive')}
            className={`px-4 py-2 rounded-full focus:outline-none ${
              filter === 'archive'
                ? 'bg-blue-500 text-white'
                : 'bg-transparent text-gray-700 hover:bg-gray-300'
            } transition`}
          >
            Archived
          </button>
        </div>
        <button
          onClick={() => setIsModalOpen(true)}
          className="bg-green-500 text-white px-4 py-2 rounded-lg shadow hover:bg-green-600 transition"
        >
          + Add List
        </button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredLists.map((list) => (
          <div
            key={list.id}
            onClick={() => goToShoppingListDetail(list)}
            className="relative p-4 bg-gray-100 rounded-lg shadow-md hover:shadow-lg hover:bg-gray-200 transition cursor-pointer"
          >
            <div
              className="absolute top-[-5px] left-[-5px] group cursor-pointer"
              onClick={(e) => {
                e.stopPropagation();
                toggleArchive(list.id);
              }}
            >
              <div
                className={`w-6 h-6 rounded-full border-2 flex items-center justify-center transition ${
                  list.archived
                    ? 'border-blue-500 bg-blue-500 text-white'
                    : 'border-gray-300 bg-white text-gray-400 hover:border-blue-500 hover:bg-blue-100'
                }`}
              >
                ✓
              </div>
            </div>
            <h2 className="text-lg font-bold text-gray-800 truncate">{list.name}</h2>
            <div className="mt-2 text-sm text-gray-600">
              <p>Active Items: {list.items.filter((item) => !item.resolved).length}</p>
              <p>Resolved Items: {list.items.filter((item) => item.resolved).length}</p>
            </div>
            {currentUser.role === 'owner' && (
              <div
                className="absolute top-[-5px] right-[-5px] group cursor-pointer"
                onClick={(e) => {
                  e.stopPropagation();
                  setConfirmDeleteList(list.id);
                }}
              >
                <button className="w-6 h-6 rounded-full text-gray-500 bg-white border-2 border-gray-300 flex items-center justify-center hover:text-red-500 hover:border-red-500 hover:bg-red-100 transition">
                  ✖
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2 className="text-lg font-bold mb-4 text-gray-800">Add New Shopping List</h2>
        <input
          type="text"
          value={newListName}
          onChange={(e) => setNewListName(e.target.value)}
          placeholder="Enter list name"
          className="p-3 border border-gray-300 rounded-lg shadow-sm w-full mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={addNewList}
          className="w-full bg-green-500 text-white px-5 py-2 rounded-lg shadow hover:bg-green-600 transition"
        >
          Add List
        </button>
      </Modal>
      <Modal
        isOpen={confirmDeleteList !== null}
        onClose={() => setConfirmDeleteList(null)}
      >
        <h2 className="text-lg font-bold mb-4 text-gray-800">Confirm Delete</h2>
        <p className="text-gray-700 mb-6">
          Are you sure you want to delete this shopping list? This action cannot be undone.
        </p>
        <div className="flex justify-end gap-4">
          <button
            onClick={() => setConfirmDeleteList(null)}
            className="bg-gray-300 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-400 transition"
          >
            Cancel
          </button>
          <button
            onClick={deleteList}
            className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
          >
            Delete
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default MainPage;
