import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getShoppingListById, updateShoppingList } from "../api";

const ShoppingListDetail = () => {
  const { id } = useParams(); // Get shopping list ID from the URL
  const navigate = useNavigate();

  const [list, setList] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [members, setMembers] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [newMember, setNewMember] = useState("");
  const [filter, setFilter] = useState("active");
  const [hoveredRow, setHoveredRow] = useState(null);
  const [hoverType, setHoverType] = useState(null);
  const [hoveredRowType, setHoveredRowType] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchList = async () => {
      try {
        const data = await getShoppingListById(id);
        setList(data);
        setListName(data.name);
        setItems(data.items);
        setMembers(data.members);
        setCurrentUser({ id: 1, name: "Owner", role: "owner" }); // Default to 'owner'
      } catch (err) {
        setError("Failed to load the shopping list.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchList();
  }, [id]);

  const handleListNameChange = async (e) => {
    const updatedName = e.target.value;
    setListName(updatedName);
    try {
      await updateShoppingList(id, { name: updatedName });
    } catch {
      setError("Failed to update the list name.");
    }
  };

  const addItem = async () => {
    if (newItem.trim() !== "") {
      const updatedItems = [...items, { id: items.length + 1, name: newItem, resolved: false }];
      setItems(updatedItems);
      setNewItem("");
      try {
        await updateShoppingList(id, { items: updatedItems });
      } catch {
        setError("Failed to add item.");
      }
    }
  };

  const addMember = async () => {
    if (currentUser.role === "owner" && newMember.trim() !== "") {
      const updatedMembers = [...members, newMember];
      setMembers(updatedMembers);
      setNewMember("");
      try {
        await updateShoppingList(id, { members: updatedMembers });
      } catch {
        setError("Failed to add member.");
      }
    }
  };

  const removeItem = async (itemId) => {
    const updatedItems = items.filter((item) => item.id !== itemId);
    setItems(updatedItems);
    try {
      await updateShoppingList(id, { items: updatedItems });
    } catch {
      setError("Failed to remove item.");
    }
  };

  const removeMember = async (member) => {
    if (currentUser.role === "owner") {
      const updatedMembers = members.filter((m) => m !== member);
      setMembers(updatedMembers);
      try {
        await updateShoppingList(id, { members: updatedMembers });
      } catch {
        setError("Failed to remove member.");
      }
    }
  };

  const toggleResolved = async (itemId) => {
    const updatedItems = items.map((item) =>
      item.id === itemId ? { ...item, resolved: !item.resolved } : item
    );
    setItems(updatedItems);
    try {
      await updateShoppingList(id, { items: updatedItems });
    } catch {
      setError("Failed to toggle item resolution.");
    }
  };

  const filteredItems = items.filter(
    (item) => (filter === "active" ? !item.resolved : item.resolved)
  );

  const leaveList = () => {
    navigate("/");
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-4 sm:p-8 rounded-lg shadow-lg">
      <header className="flex items-center justify-between mb-6 border-b pb-4 relative">
        <button
          onClick={() => navigate('/')}
          className="text-xl sm:text-2xl text-gray-700 hover:text-gray-900 transition absolute left-0"
        >
          ←
        </button>
        <h1 className="text-lg sm:text-3xl font-semibold text-gray-800 mx-auto">
          {currentUser.role === 'owner' ? (
            <input
              type="text"
              value={listName}
              onChange={handleListNameChange}
              className="border-b border-gray-300 focus:outline-none focus:border-blue-500"
            />
          ) : (
            listName
          )}
        </h1>
        <div className="absolute top-2 right-4">
          <select
            value={currentUser.id}
            onChange={(e) =>
              setCurrentUser(users.find((user) => user.id === parseInt(e.target.value)))
            }
            className="p-1 sm:p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {users.map((user) => (
              <option key={user.id} value={user.id}>
                {user.name} ({user.role})
              </option>
            ))}
          </select>
        </div>
      </header>

      <div className="mb-8">
        <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center mb-4">
          <h2 className="text-xl sm:text-2xl font-medium mb-2 sm:mb-0">Items:</h2>
          <div className="flex bg-gray-200 rounded-full p-1">
            <button
              onClick={() => setFilter('active')}
              className={`px-2 sm:px-4 py-1 sm:py-2 rounded-full focus:outline-none ${
                filter === 'active'
                  ? 'bg-blue-500 text-white'
                  : 'bg-transparent text-gray-700 hover:bg-gray-300'
              } transition`}
            >
              Active Items
            </button>
            <button
              onClick={() => setFilter('archive')}
              className={`px-2 sm:px-4 py-1 sm:py-2 rounded-full focus:outline-none ${
                filter === 'archive'
                  ? 'bg-blue-500 text-white'
                  : 'bg-transparent text-gray-700 hover:bg-gray-300'
              } transition`}
            >
              Archived Items
            </button>
          </div>
        </div>
        <div className="flex flex-col sm:flex-row items-center gap-4 mb-4">
          <input
            type="text"
            value={newItem}
            onChange={(e) => setNewItem(e.target.value)}
            placeholder="New item"
            className="flex-grow p-2 sm:p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={addItem}
            className="bg-green-500 text-white px-4 sm:px-5 py-2 sm:py-3 rounded-lg shadow hover:bg-green-600 transition w-full sm:w-48"
          >
            + New Item
          </button>
        </div>
        <ul className="divide-y divide-gray-200">
          {filteredItems.map((item) => (
            <li
              key={item.id}
              className={`flex justify-between items-center py-2 sm:py-3 ${
                hoveredRow === item.id && hoveredRowType === 'items'
                  ? hoverType === 'delete'
                    ? 'bg-red-100'
                    : hoverType === 'archive'
                    ? 'bg-blue-100'
                    : ''
                  : ''
              }`}
            >
              <div className="flex items-center gap-3">
                <div
                  className="relative group cursor-pointer"
                  onMouseEnter={() => {
                    setHoveredRow(item.id);
                    setHoverType('archive');
                    setHoveredRowType('items');
                  }}
                  onMouseLeave={() => {
                    setHoveredRow(null);
                    setHoverType(null);
                    setHoveredRowType(null);
                  }}
                  onClick={() => toggleResolved(item.id)}
                >
                   <div
                className={`w-6 h-6 rounded-full border-2 flex items-center justify-center transition ${
                  item.resolved
                    ? 'border-blue-500 bg-blue-500 text-white'
                    : 'border-gray-300 bg-white text-gray-400 hover:border-blue-500 hover:bg-blue-100'
                }`}
              >
                ✓
              </div>
                  <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                    {item.resolved ? 'Unarchive' : 'Archive'}
                  </span>
                </div>
                <span
                  className={`${
                    item.resolved ? 'line-through text-gray-500' : 'text-gray-800'
                  }`}
                >
                  {item.name}
                </span>
              </div>
              <div
                className="relative group cursor-pointer"
                onMouseEnter={() => {
                  setHoveredRow(item.id);
                  setHoverType('delete');
                  setHoveredRowType('items');
                }}
                onMouseLeave={() => {
                  setHoveredRow(null);
                  setHoverType(null);
                  setHoveredRowType(null);
                }}
                onClick={() => removeItem(item.id)}
              >
                <button className="w-6 h-6 rounded-full text-gray-500 bg-white border-2 border-gray-300 flex items-center justify-center hover:text-red-500 hover:border-red-500 hover:bg-red-100 transition">
                  ✖
                </button>
                <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                  Delete
                </span>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <div className="flex justify-between items-center mb-4">
      <h2 className="text-xl sm:text-2xl font-medium">Members:</h2>
        {currentUser.role === 'guest' && (
          <button
            onClick={leaveList}
            className="bg-red-500 text-white px-4 py-2 rounded-lg shadow hover:bg-red-600 transition text-sm sm:text-base"
          >
            Leave List
          </button>
        )}
      </div>
      {currentUser.role === 'owner' && (
        <div className="flex flex-col sm:flex-row items-center gap-4 mb-4">
          <input
            type="text"
            value={newMember}
            onChange={(e) => setNewMember(e.target.value)}
            placeholder="New member"
            className="flex-grow p-2 sm:p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={addMember}
            className="bg-blue-500 text-white px-4 sm:px-5 py-2 sm:py-3 rounded-lg shadow hover:bg-blue-600 transition w-full sm:w-48"
          >
            + New Member
          </button>
        </div>
      )}
      <ul className="divide-y divide-gray-200">
        {members.map((member, index) => (
          <li
            key={index}
            className={`flex justify-between items-center py-2 sm:py-3 ${
              hoveredRow === index && hoveredRowType === 'members'
                ? hoverType === 'delete'
                  ? 'bg-red-100'
                  : ''
                : ''
            }`}
          >
            <span className="text-gray-800">{member}</span>
            {currentUser.role === 'owner' && (
              <div
                className="relative group cursor-pointer"
                onMouseEnter={() => {
                  setHoveredRow(index);
                  setHoverType('delete');
                  setHoveredRowType('members');
                }}
                onMouseLeave={() => {
                  setHoveredRow(null);
                  setHoverType(null);
                  setHoveredRowType(null);
                }}
                onClick={() => removeMember(member)}
              >
                <button className="w-6 h-6 rounded-full text-gray-500 bg-white border-2 border-gray-300 flex items-center justify-center hover:text-red-500 hover:border-red-500 hover:bg-red-100 transition">
                  ✖
                </button>
                <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                  Delete
                </span>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListDetail;
