import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getShoppingListById, updateShoppingList } from "../api";

const ShoppingListDetail = () => {
  const { id } = useParams(); // Get shopping list ID from the URL
  const navigate = useNavigate();

  const [listName, setListName] = useState("");
  const [items, setItems] = useState([]);
  const [members, setMembers] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [newMember, setNewMember] = useState("");
  const [filter, setFilter] = useState("active");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchList = async () => {
      try {
        const data = await getShoppingListById(id);
        setListName(data.name);
        setItems(data.items);
        setMembers(data.members);
      } catch (err) {
        setError("Failed to load the shopping list.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchList();
  }, [id]);

  const handleListNameChange = async (e) => {
    const updatedName = e.target.value;
    setListName(updatedName);
    try {
      await updateShoppingList(id, { name: updatedName });
    } catch {
      setError("Failed to update the list name.");
    }
  };

  const addItem = async () => {
    if (newItem.trim() !== "") {
      const updatedItems = [...items, { id: items.length + 1, name: newItem, resolved: false }];
      setItems(updatedItems);
      setNewItem("");
      try {
        await updateShoppingList(id, { items: updatedItems });
      } catch {
        setError("Failed to add item.");
      }
    }
  };

  const addMember = async () => {
    if (newMember.trim() !== "") {
      const updatedMembers = [...members, newMember];
      setMembers(updatedMembers);
      setNewMember("");
      try {
        await updateShoppingList(id, { members: updatedMembers });
      } catch {
        setError("Failed to add member.");
      }
    }
  };

  const removeItem = async (itemId) => {
    const updatedItems = items.filter((item) => item.id !== itemId);
    setItems(updatedItems);
    try {
      await updateShoppingList(id, { items: updatedItems });
    } catch {
      setError("Failed to remove item.");
    }
  };

  const removeMember = async (member) => {
    const updatedMembers = members.filter((m) => m !== member);
    setMembers(updatedMembers);
    try {
      await updateShoppingList(id, { members: updatedMembers });
    } catch {
      setError("Failed to remove member.");
    }
  };

  const toggleResolved = async (itemId) => {
    const updatedItems = items.map((item) =>
      item.id === itemId ? { ...item, resolved: !item.resolved } : item
    );
    setItems(updatedItems);
    try {
      await updateShoppingList(id, { items: updatedItems });
    } catch {
      setError("Failed to toggle item resolution.");
    }
  };

  const filteredItems = items.filter(
    (item) => (filter === "active" ? !item.resolved : item.resolved)
  );

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-4 sm:p-8 rounded-lg shadow-lg">
      <header className="flex items-center justify-between mb-6 border-b pb-4">
        <button
          onClick={() => navigate("/")}
          className="text-xl sm:text-2xl text-gray-700 hover:text-gray-900 transition"
        >
          ← Back
        </button>
        <h1 className="text-lg sm:text-3xl font-semibold text-gray-800 mx-auto">
          <input
            type="text"
            value={listName}
            onChange={handleListNameChange}
            className="border-b border-gray-300 focus:outline-none focus:border-blue-500"
          />
        </h1>
      </header>

      <div className="mb-8">
        <h2 className="text-xl font-medium mb-4">Items:</h2>
        <div className="flex gap-4 mb-4">
          <input
            type="text"
            value={newItem}
            onChange={(e) => setNewItem(e.target.value)}
            placeholder="New item"
            className="flex-grow p-2 border border-gray-300 rounded-lg"
          />
          <button onClick={addItem} className="bg-green-500 text-white px-4 py-2 rounded-lg">
            + Add Item
          </button>
        </div>
        <ul>
          {filteredItems.map((item) => (
            <li key={item.id} className="flex justify-between items-center">
              <span
                className={`cursor-pointer ${
                  item.resolved ? "line-through text-gray-500" : "text-gray-800"
                }`}
                onClick={() => toggleResolved(item.id)}
              >
                {item.name}
              </span>
              <button onClick={() => removeItem(item.id)} className="text-red-500">
                ✖
              </button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2 className="text-xl font-medium mb-4">Members:</h2>
        <div className="flex gap-4 mb-4">
          <input
            type="text"
            value={newMember}
            onChange={(e) => setNewMember(e.target.value)}
            placeholder="New member"
            className="flex-grow p-2 border border-gray-300 rounded-lg"
          />
          <button onClick={addMember} className="bg-blue-500 text-white px-4 py-2 rounded-lg">
            + Add Member
          </button>
        </div>
        <ul>
          {members.map((member) => (
            <li key={member} className="flex justify-between items-center">
              <span>{member}</span>
              <button onClick={() => removeMember(member)} className="text-red-500">
                ✖
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ShoppingListDetail;
