const BASE_URL = 'https://shopping-list/api';

export const fetchShoppingLists = async () => {
  const response = await fetch(`${BASE_URL}/shopping-lists`);
  return response.json();
};

export const saveShoppingList = async (list) => {
  const response = await fetch(`${BASE_URL}/shopping-lists`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(list),
  });
  return response.json();
};

export const updateShoppingList = async (id, updates) => {
  const response = await fetch(`${BASE_URL}/shopping-lists/${id}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(updates),
  });
  return response.json();
};

export const deleteShoppingList = async (id) => {
  await fetch(`${BASE_URL}/shopping-lists/${id}`, { method: 'DELETE' });
};
