import React from "react";

const ErrorAlert = ({ message, onRetry }) => {
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="max-w-md w-full bg-red-100 border border-red-400 text-red-700 p-4 rounded-lg shadow-lg relative animate-fade-in">
        <strong className="text-lg font-bold">Error</strong>
        <p className="mt-2">{message}</p>
        {onRetry && (
          <button
            onClick={onRetry}
            className="mt-4 px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition duration-200"
          >
            Retry
          </button>
        )}
      </div>
    </div>
  );
};

export default ErrorAlert;
