import React from 'react';

const ArchiveButton = ({ isArchived, onClick }) => {
  return (
    <div
      className="group relative cursor-pointer"
      onClick={(e) => {
        e.stopPropagation();
        onClick();
      }}
    >
      <div
        className={`w-6 h-6 rounded-full border-2 flex items-center justify-center transition ${
          isArchived
            ? 'border-blue-500 bg-blue-500 text-white'
            : 'border-gray-300 bg-white text-gray-400 hover:border-blue-500 hover:bg-blue-100'
        }`}
      >
        âœ“
      </div>
      <span className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
        {isArchived ? 'Unarchive' : 'Archive'}
      </span>
    </div>
  );
};

export default ArchiveButton;
