import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ShoppingListDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const users = [
    { id: 1, name: 'Owner', role: 'owner' },
    { id: 2, name: 'Guest', role: 'guest' },
  ];
  const defaultList = {
    name: 'Default Shopping List',
    items: [
      { id: 1, name: 'Default Item 1', resolved: false },
      { id: 2, name: 'Default Item 2', resolved: false },
      { id: 3, name: 'Default Item 3', resolved: true },
    ],
    members: ['Member 1', 'Member 2', 'Member 3'],
  };
  const defaultUser = users[0];

  const { list = defaultList, passedUser = defaultUser } = location.state || {};
  const [currentUser, setCurrentUser] = useState(passedUser);
  const [listName, setListName] = useState(list.name);
  const [newItem, setNewItem] = useState('');
  const [newMember, setNewMember] = useState('');
  const [items, setItems] = useState(list?.items || []);
  const [members, setMembers] = useState(list?.members || []);
  const [filter, setFilter] = useState('active');
  const [hoveredRow, setHoveredRow] = useState(null);
  const [hoverType, setHoverType] = useState(null); // 'delete' or 'archive'

  const handleListNameChange = (e) => {
    if (currentUser.role === 'owner') {
      setListName(e.target.value);
    }
  };

  const addItem = () => {
    if (newItem) {
      setItems([...items, { id: items.length + 1, name: newItem, resolved: false }]);
      setNewItem('');
    }
  };

  const toggleResolved = (id) => {
    setItems(
      items.map((item) =>
        item.id === id ? { ...item, resolved: !item.resolved } : item
      )
    );
  };

  const removeItem = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  const filteredItems = items.filter(
    (item) => (filter === 'active' ? !item.resolved : item.resolved)
  );

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-4 sm:p-8 rounded-lg shadow-lg">
      <header className="flex items-center justify-between mb-6 border-b pb-4 relative">
        <button
          onClick={() => navigate('/')}
          className="text-xl sm:text-2xl text-gray-700 hover:text-gray-900 transition absolute left-0"
        >
          ←
        </button>
        <h1 className="text-lg sm:text-3xl font-semibold text-gray-800 mx-auto">
          {currentUser.role === 'owner' ? (
            <input
              type="text"
              value={listName}
              onChange={handleListNameChange}
              className="border-b border-gray-300 focus:outline-none focus:border-blue-500"
            />
          ) : (
            listName
          )}
        </h1>
      </header>

      <div className="mb-8">
        <h2 className="text-xl sm:text-2xl font-medium mb-4">Items:</h2>
        <ul className="divide-y divide-gray-200">
          {filteredItems.map((item) => (
            <li
              key={item.id}
              className={`flex justify-between items-center py-2 sm:py-3 transition ${
                hoveredRow === item.id
                  ? hoverType === 'delete'
                    ? 'bg-red-100'
                    : 'bg-blue-100'
                  : ''
              }`}
            >
              <div className="flex items-center gap-3">
                <input
                  type="checkbox"
                  checked={item.resolved}
                  onChange={() => toggleResolved(item.id)}
                  className="form-checkbox h-4 w-4 sm:h-5 sm:w-5 text-blue-600"
                />
                <span
                  className={`${
                    item.resolved ? 'line-through text-gray-500' : 'text-gray-800'
                  }`}
                >
                  {item.name}
                </span>
              </div>

              <div className="flex gap-4">
                {/* Archive Button */}
                <div
                  className="relative group cursor-pointer"
                  onMouseEnter={() => {
                    setHoveredRow(item.id);
                    setHoverType('archive');
                  }}
                  onMouseLeave={() => setHoveredRow(null)}
                  onClick={() => toggleResolved(item.id)}
                >
                  <button
                    className={`w-6 h-6 rounded-full flex items-center justify-center transition ${
                      item.resolved
                        ? 'bg-blue-500 text-white'
                        : 'bg-gray-100 text-gray-500 hover:bg-blue-100 hover:text-blue-500'
                    }`}
                  >
                    ✓
                  </button>
                  <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                    {item.resolved ? 'Unarchive' : 'Archive'}
                  </span>
                </div>

                {/* Delete Button */}
                <div
                  className="relative group cursor-pointer"
                  onMouseEnter={() => {
                    setHoveredRow(item.id);
                    setHoverType('delete');
                  }}
                  onMouseLeave={() => setHoveredRow(null)}
                  onClick={() => removeItem(item.id)}
                >
                  <button className="w-6 h-6 rounded-full text-gray-500 bg-gray-100 border border-gray-300 flex items-center justify-center hover:text-red-500 hover:bg-red-100 hover:border-red-500 transition">
                    ✖
                  </button>
                  <span className="absolute bottom-full left-1/2 -translate-x-1/2 mb-1 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                    Delete
                  </span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ShoppingListDetail;
