import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import data from './components/data.json';

const MainPage = () => {
  const navigate = useNavigate();
  const [shoppingLists, setShoppingLists] = useState(data.shoppingLists);

  const goToDetail = (list) => {
    navigate(`/shopping-list/${list.id}`, { state: { list } });
  };

  const toggleArchive = (id) => {
    setShoppingLists((lists) =>
      lists.map((list) =>
        list.id === id ? { ...list, archived: !list.archived } : list
      )
    );
  };

  const deleteList = (id) => {
    setShoppingLists((lists) => lists.filter((list) => list.id !== id));
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">Shopping Lists</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {shoppingLists.map((list) => (
          <div
            key={list.id}
            className="p-4 bg-gray-100 rounded-lg shadow-md hover:shadow-lg transition"
          >
            <h3 className="text-lg font-bold mb-2">{list.name}</h3>
            <p className="text-sm text-gray-600">
              {list.archived ? 'Archived' : 'Active'}
            </p>
            <div className="mt-4 flex justify-between items-center">
              <button
                onClick={() => goToDetail(list)}
                className="text-blue-500 hover:underline"
              >
                View Details
              </button>
              <div className="flex gap-2">
                <button
                  onClick={() => toggleArchive(list.id)}
                  className="text-blue-500 hover:underline"
                >
                  {list.archived ? 'Unarchive' : 'Archive'}
                </button>
                <button
                  onClick={() => deleteList(list.id)}
                  className="text-red-500 hover:underline"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainPage;
