import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import data from './data.json';

const MainPage = () => {
  const navigate = useNavigate();
  const [shoppingLists, setShoppingLists] = useState(data.shoppingLists);
  const [filter, setFilter] = useState('active');

  const goToDetail = (list) => {
    navigate(`/shopping-list/${list.id}`, { state: { list } });
  };

  const toggleArchive = (id) => {
    setShoppingLists((lists) =>
      lists.map((list) =>
        list.id === id ? { ...list, archived: !list.archived } : list
      )
    );
  };

  const deleteList = (id) => {
    setShoppingLists((lists) => lists.filter((list) => list.id !== id));
  };

  const filteredLists = shoppingLists.filter(
    (list) => (filter === 'active' ? !list.archived : list.archived)
  );

  return (
    <div className="max-w-6xl mx-auto bg-white p-6 sm:p-8 rounded-lg shadow-lg">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Shopping Lists</h1>
        <button
          onClick={() => alert('Add new list (Modal coming soon!)')}
          className="bg-green-500 text-white px-5 py-2 rounded-lg shadow hover:bg-green-600 hover:scale-105 transition"
        >
          + Add New List
        </button>
      </header>

      <div className="flex items-center justify-between mb-6">
        <div className="flex bg-gray-200 rounded-full p-1">
          <button
            onClick={() => setFilter('active')}
            className={`px-4 py-2 rounded-full ${
              filter === 'active'
                ? 'bg-blue-500 text-white shadow'
                : 'bg-transparent text-gray-700 hover:bg-gray-300'
            } transition`}
          >
            Active
          </button>
          <button
            onClick={() => setFilter('archive')}
            className={`px-4 py-2 rounded-full ${
              filter === 'archive'
                ? 'bg-blue-500 text-white shadow'
                : 'bg-transparent text-gray-700 hover:bg-gray-300'
            } transition`}
          >
            Archived
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredLists.map((list) => (
          <div
            key={list.id}
            className="p-6 bg-gray-100 rounded-lg shadow-md hover:shadow-lg transition transform hover:scale-105"
          >
            <h3 className="text-lg font-bold text-gray-700 mb-2">{list.name}</h3>
            <p className="text-sm text-gray-500 mb-4">
              {list.archived ? 'Archived' : 'Active'}
            </p>
            <div className="flex justify-between items-center">
              <button
                onClick={() => goToDetail(list)}
                className="text-blue-500 hover:text-blue-600 hover:scale-105 transition"
              >
                View Details
              </button>
              <div className="flex gap-2">
                <button
                  onClick={() => toggleArchive(list.id)}
                  className={`px-4 py-2 rounded-md text-sm font-medium ${
                    list.archived
                      ? 'bg-yellow-400 text-gray-800 hover:bg-yellow-500'
                      : 'bg-blue-400 text-white hover:bg-blue-500'
                  } shadow-md hover:scale-105 transition`}
                >
                  {list.archived ? 'Unarchive' : 'Archive'}
                </button>
                <button
                  onClick={() => deleteList(list.id)}
                  className="px-4 py-2 bg-red-500 text-white rounded-md shadow-md hover:bg-red-600 hover:scale-105 transition"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainPage;
