import React, { useContext, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { UserContext } from './UserContext';
import Modal from './Modal';

const ShoppingListDetail = () => {
  const { currentUser } = useContext(UserContext);
  const navigate = useNavigate();
  const location = useLocation();
  const { list } = location.state;

  const [shoppingList, setShoppingList] = useState(list);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleArchive = () => {
    setShoppingList((prevList) => ({
      ...prevList,
      archived: !prevList.archived,
    }));
  };

  const deleteList = () => {
    // Handle delete logic (e.g., API call or navigation)
    navigate('/');
  };

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-8 rounded-lg shadow-lg">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">{shoppingList.name}</h1>
        <div className="flex gap-2">
          <div
            className="group cursor-pointer"
            onClick={toggleArchive}
          >
            <div
              className={`w-8 h-8 rounded-full border-2 flex items-center justify-center transition ${
                shoppingList.archived
                  ? 'border-blue-500 bg-blue-500 text-white'
                  : 'border-gray-300 bg-white text-gray-400 hover:border-blue-500 hover:bg-blue-100'
              }`}
            >
              ✓
            </div>
            <span className="absolute bottom-full right-0 mb-2 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
              {shoppingList.archived ? 'Unarchive' : 'Archive'}
            </span>
          </div>
          {currentUser.role === 'owner' && (
            <div
              className="group cursor-pointer"
              onClick={() => setIsModalOpen(true)}
            >
              <button className="w-8 h-8 rounded-full text-gray-500 bg-white border-2 border-gray-300 flex items-center justify-center hover:text-red-500 hover:border-red-500 hover:bg-red-100 transition">
                ✖
              </button>
              <span className="absolute bottom-full right-0 mb-2 opacity-0 group-hover:opacity-100 text-xs bg-gray-700 text-white rounded py-1 px-2 shadow-lg transition">
                Delete
              </span>
            </div>
          )}
        </div>
      </header>
      <div>
        <h2 className="text-lg font-bold mb-4">Items</h2>
        <ul className="list-disc ml-6">
          {shoppingList.items.map((item) => (
            <li key={item.id} className={item.resolved ? 'line-through' : ''}>
              {item.name}
            </li>
          ))}
        </ul>
      </div>

      {/* Delete Confirmation Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2 className="text-lg font-bold mb-4 text-gray-800">Confirm Delete</h2>
        <p className="text-gray-700 mb-6">
          Are you sure you want to delete this shopping list? This action cannot be undone.
        </p>
        <div className="flex justify-end gap-4">
          <button
            onClick={() => setIsModalOpen(false)}
            className="bg-gray-300 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-400 transition"
          >
            Cancel
          </button>
          <button
            onClick={deleteList}
            className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
          >
            Delete
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ShoppingListDetail;
