import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ShoppingListDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const users = [
    { id: 1, name: 'Owner', role: 'owner' },
    { id: 2, name: 'Guest', role: 'guest' },
  ];
  const defaultList = {
    name: 'Default Shopping List',
    items: [
      { id: 1, name: 'Default Item 1', resolved: false },
      { id: 2, name: 'Default Item 2', resolved: false },
      { id: 3, name: 'Default Item 3', resolved: true },
    ],
    members: ['Member 1', 'Member 2', 'Member 3'],
  };
  const defaultUser = users[0];

  const { list = defaultList, passedUser = defaultUser } = location.state || {};
  const [currentUser, setCurrentUser] = useState(passedUser);
  const [listName, setListName] = useState(list.name);
  const [newItem, setNewItem] = useState('');
  const [newMember, setNewMember] = useState('');
  const [items, setItems] = useState(list?.items || []);
  const [members, setMembers] = useState(list?.members || []);
  const [filter, setFilter] = useState('active');

  const handleListNameChange = (e) => {
    if (currentUser.role === 'owner') {
      setListName(e.target.value);
    }
  };

  const addItem = () => {
    if (newItem) {
      setItems([...items, { id: items.length + 1, name: newItem, resolved: false }]);
      setNewItem('');
    }
  };

  const addMember = () => {
    if (currentUser.role === 'owner' && newMember) {
      setMembers([...members, newMember]);
      setNewMember('');
    }
  };

  const removeItem = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  const removeMember = (member) => {
    if (currentUser.role === 'owner') {
      setMembers(members.filter((m) => m !== member));
    }
  };

  const toggleResolved = (id) => {
    setItems(
      items.map((item) =>
        item.id === id ? { ...item, resolved: !item.resolved } : item
      )
    );
  };

  const leaveList = () => {
    navigate('/');
  };

  const filteredItems = items.filter(
    (item) => (filter === 'active' ? !item.resolved : item.resolved)
  );

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      {filteredLists.map((list) => (
        <div
          key={list.id}
          onClick={() => goToShoppingListDetail(list)}
          className="relative p-4 bg-gray-100 rounded-lg shadow-md hover:shadow-lg hover:bg-gray-200 transition cursor-pointer"
        >
          <h2 className="text-lg font-bold text-gray-800 truncate">{list.name}</h2>
          <div className="absolute top-[-10px] left-[-10px]">
            <ArchiveButton
              isArchived={list.archived}
              onClick={() => toggleArchive(list.id)}
            />
          </div>
          <div className="absolute top-[-10px] right-[-10px]">
            <DeleteButton onClick={() => setConfirmDeleteList(list.id)} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default MainPage;
