import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import ArchiveButton from './ArchiveButton';
import DeleteButton from './DeleteButton';

const ShoppingListDetail = () => {
  // ... existing code ...

  return (
    <div className="max-w-4xl w-full mx-auto bg-white p-4 sm:p-8 rounded-lg shadow-lg">
      {/* Other elements */}
      <ul className="divide-y divide-gray-200">
        {items.map((item) => (
          <li key={item.id} className="flex justify-between items-center py-2 sm:py-3">
            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={item.resolved}
                onChange={() => toggleResolved(item.id)}
                className="form-checkbox h-4 w-4 sm:h-5 sm:w-5 text-blue-600"
              />
              <span
                className={`${
                  item.resolved ? 'line-through text-gray-500' : 'text-gray-800'
                }`}
              >
                {item.name}
              </span>
            </div>
            <div className="flex gap-2">
              <ArchiveButton
                isArchived={item.resolved}
                onClick={() => toggleResolved(item.id)}
              />
              <DeleteButton onClick={() => removeItem(item.id)} />
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShoppingListDetail;
